program.script:
var TOTAL_SENSORS = 2;
var sensorNum = 0; //Keeps track of sensor updating
var imgList = [];

handler.behavior:
function onInvoke( handler, message ){
	var result = message.requestObject;  
	application.distribute( "onAnalogValueChanged", result ); 		
}

behavior.method:
this.data = data;
container.add(new Text({left:5, right:0, top:30, bottom:0}, color2, sensor, "Light Sensor 1: "));
container.add(new Text({left:5, right:0, top:65, bottom:0}, color2, sensor, "Light Sensor 2: "));


			//Code to read light sensor data
			application.invoke( new MessageWithObject( "pins:/lightSensor1/read?" + 
	            serializeQuery( {       
					repeat: "off",
					callback: "/updatePic"
        	} ) ) );
			application.invoke( new MessageWithObject( "pins:/lightSensor2/read?" + 
	            serializeQuery( {       
					repeat: "off",
					callback: "/updatePic"
        	} ) ) );
			//Code to select right sound then play it

if(result < 0.5)
{
	imgList[sensorNum] = new Picture({left:70, right:220, top:30 + 25 * sensorNum, bottom:190 - 25 * sensorNum, height:25, width:25}, "media/moon.jpeg");
}
else
{
	imgList[sensorNum] = new Picture({left:70, right:220, top:30 + 25 * sensorNum, bottom:190 - 25 * sensorNum, height:25, width:25}, "media/sun.jpe");
}
container.add(imgList[sensorNum]);
sensorNum++;
if(sensorNum >= TOTAL_SENSORS)
{
	sensorNum = 0;
}

program.script:

			application.invoke( new MessageWithObject( "pins:configure", {
        	lightSensor1: {
                require: "lightSensor",
                pins: {
                    analog: { pin: 52 }
                }
            },
            lightSensor2: {
            	require: "lightSensor",
            	pins: {
            		analog: {pin: 55 }
            	}
            }
       		}));


        	application.add( new body({text:"List of Lights"}) );
